import { useRef, useState } from "react";

const DiaryItem = ({author,content,emotion,created_date,id, onRemove ,onEdit})=>{

    const contentDiv = useRef();
    const editTextarea = useRef();
    const deleteButton = useRef();
    const saveButton = useRef();
    const dontEdit = useRef();
    const editButton = useRef();

    const [isEdit,setisEdit] = useState(false);
    const toggleisEdit = ()=>{
        setisEdit(!isEdit);
    }

    const styleDisplay = (style1,style2,style3,style4,style5,style6)=>{ // 버튼 클릭 시 스타일 수정
        contentDiv.current.style.display = style1;
        editTextarea.current.style.display = style2;
        saveButton.current.style.display = style3;
        deleteButton.current.style.display = style4;
        dontEdit.current.style.display = style5;
        editButton.current.style.display = style6;
    }
    return(
        <div className="DiaryItem">
            <div className="info">
            <span >
                작성자 : {author} | 감정 점수 : {emotion}
            </span>
            <br/>
            <span className="date">
                {new Date(created_date).toLocaleString()}
            </span>
            </div>
            <br/>
            <div className="content" ref={contentDiv}>{content}</div>
            <div> 
            <textarea className="editTextarea" ref={editTextarea} style={{display : 'none'}}></textarea>
            </div>
            <div>
            <span>
                <button ref={deleteButton} onClick={()=>{
                    if(window.confirm("정말 일기를 삭제하시겠습니까?")){
                        onRemove(id);
                    }
                }}>삭제하기</button>
            </span>
            <span>
                <button ref={editButton} onClick={()=>{
                    styleDisplay('none','block','inline','none','inline','none')
                    editTextarea.current.value = content // 처음 수정하기를 눌렀을 때 value가 채워져있게
                }}>수정하기</button>                
            </span>
            <span>
            <button ref={dontEdit} style={{display : 'none'}} onClick={()=>{
                styleDisplay('block','none','none','inline','none','inline')
            }}>수정 취소하기</button>
            </span>
            <span>
            <button ref={saveButton} style={{display : 'none'} } onClick={()=>{
                styleDisplay('block','none','none','inline','none','inline')
                onEdit(id,editTextarea.current.value); // 배열 수정 함수 호출
            }}>저장하기</button>
            </span>
            </div>
        </div>
    )
}

export default DiaryItem;
