import { useRef } from "react";

const DiaryItem = ({author,content,emotion,created_date,id, onDelete ,onEdit})=>{
    const contentDiv = useRef();
    const editTextarea = useRef();
    const deleteButton = useRef();
    const saveButton = useRef();
    const dontEdit = useRef();
    const editButton = useRef();

    return(
        <div className="DiaryItem">
            <div className="info">
            <span >
                작성자 : {author} | 감정 점수 : {emotion}
            </span>
            <br/>
            <span className="date">
                {new Date(created_date).toLocaleString()}
            </span>
            </div>
            <br/>
            <div className="content" ref={contentDiv}>{content}</div>
            <div> 
            <textarea className="editTextarea" ref={editTextarea} style={{display : 'none'}}></textarea>
            </div>
            <div>
            <span>
                <button ref={deleteButton} onClick={()=>{
                    if(window.confirm("정말 일기를 삭제하시겠습니까?")){
                        onDelete(id);
                    }
                }}>삭제하기</button>
            </span>
            <span>
                <button ref={editButton} onClick={()=>{
                    contentDiv.current.style.display = 'none';
                    editTextarea.current.style.display = 'block';
                    saveButton.current.style.display = 'inline';
                    deleteButton.current.style.display = 'none';
                    dontEdit.current.style.display = 'inline';
                    editButton.current.style.display = 'none';
                    editTextarea.current.value = content;
                }}>수정하기</button>                
            </span>
            <span>
            <button ref={dontEdit} style={{display : 'none'}}>수정 취소하기</button>
            </span>
            <span>
            <button ref={saveButton} style={{display : 'none'} } onClick={()=>{
                contentDiv.current.style.display = 'block';
                editTextarea.current.style.display = 'none';
                saveButton.current.style.display = 'none';
                deleteButton.current.style.display = 'inline';
                dontEdit.current.style.display = 'none';
                editButton.current.style.display = 'inline';
                onEdit(id,editTextarea.current.value);
            }}>저장하기</button>
            </span>
            </div>
        </div>
    )
}

export default DiaryItem;
