import { useRef, useState } from "react";

const DiaryItem = ({author,content,emotion,created_date,id, onRemove ,onEdit})=>{

    const editTextarea = useRef();

    const [isEdit,setisEdit] = useState(false); // 모든 토글 기능은 이렇게 처리하자
    const toggleisEdit = ()=>{
        setisEdit(!isEdit);
    }

    return(
        <div className="DiaryItem">
            <div className="info">
            <span >
                작성자 : {author} | 감정 점수 : {emotion}
            </span>
            <br/>
            <span className="date">
                {new Date(created_date).toLocaleString()}
            </span>
            </div>
                {isEdit ? <div> 
                    <div>
                    <textarea ref={editTextarea} defaultValue={content} className="content" ></textarea>
                    </div><span>
            <button  onClick={toggleisEdit}>수정 취소하기</button>
            </span>
            <span>
            <button  onClick={()=>{
                toggleisEdit()
                onEdit(id,editTextarea.current.value); // 배열 수정 함수 호출
            }}>저장하기</button>
            </span> </div> 
            :
              <div>
                <div className="content">{content}</div>
                <span>
                <button onClick={()=>{
                    if(window.confirm("정말 일기를 삭제하시겠습니까?")){
                        onRemove(id);
                    }
                }}>삭제하기</button>
            </span>
            <span>
                <button onClick={toggleisEdit}>수정하기</button>                
            </span> </div>}
            </div>
    )
}

export default DiaryItem;
