import { useRef } from "react";

const DiaryItem = ({author,content,emotion,created_date,id, onDelete})=>{
    const contentDiv = useRef();
    const editTextarea = useRef();
    return(
        <div className="DiaryItem">
            <div className="info">
            <span >
                작성자 : {author} | 감정 점수 : {emotion}
            </span>
            <br/>
            <span className="date">
                {new Date(created_date).toLocaleString()}
            </span>
            </div>
            <br/>
            <div className="content" ref={contentDiv}>{content}</div>
            <div> 
            <textarea className="editTextarea" ref={editTextarea} style={{display : 'none'}}></textarea>
            </div>
            <div>
            <span>
                <button onClick={()=>{
                    if(window.confirm("정말 일기를 삭제하시겠습니까?")){
                        onDelete(id);
                    }
                }}>삭제하기</button>
            </span>
            <span>
                <button onClick={()=>{
                    contentDiv.current.style.display = 'none';
                    editTextarea.current.style.display = 'block';
                }}>수정하기</button>
            </span>
            </div>
        </div>
    )
}

export default DiaryItem;
