import { useState } from "react";

const DiaryEditor = ()=>{

    // 상태 변화 과정이 동일하다면 묶어줄 수 있다.
    const [state,setstate] = useState({ 
        author : "",
        content : ""
    }); // 초깃값으로 객체를 전달한다. 이 객체는 state에 할당됨.

    const onChanges = (e,a)=>{
        setstate({ // setstate()의 인자값은 그대로 state에 할당된다는 것을 기억하자.
            ...state,
            a : e.target.value,
        });
    }

    return (
    <div className="DiaryEditor">
        <h2>오늘의 일기</h2>
        <div>
            <input name="author" value={state.author} onChange={onChanges(e,state.author)}/>
        </div>
        <div>
            <textarea name="content" value={state.content} onChange={onChanges(e,state.content)}/>
        </div>
    </div>
    )
}

export default DiaryEditor;
